function secondsToMs(seconds: number) {
    if (seconds >= 0) {
        minutes = Math.floor(seconds / 60)
        remainingSeconds = seconds % 60
        return "" + minutes + ":" + (remainingSeconds < 10 ? "0" : "") + remainingSeconds
    } else {
        return "0:00"
    }
}
